import edu.stanford.nlp.ling.CoreAnnotations
import edu.stanford.nlp.neural.rnn.RNNCoreAnnotations
import edu.stanford.nlp.pipeline.StanfordCoreNLP
import edu.stanford.nlp.sentiment.SentimentCoreAnnotations

import scala.collection.JavaConverters._

object Sentiment extends Enumeration {
  type Sentiment = Value
  val VeryNegative, Negative, Neutral, Positive, VeryPositive = Value
}

object Functions {
  @transient private var sentimentPipeline: StanfordCoreNLP = _

  private def getOrCreateSentimentPipeline(): StanfordCoreNLP = {
    if (sentimentPipeline == null) {
      val props = new Properties()
      props.setProperty("annotators", "tokenize, ssplit, parse, sentiment")
      sentimentPipeline = new StanfordCoreNLP(props)
    }
    sentimentPipeline
  }

  private def calcSentiment(text: String): Int = {
    val pipeline = getOrCreateSentimentPipeline()
    val annotation = sentimentPipeline.process(text)
    val tree = annotation.get(classOf[CoreAnnotations.SentencesAnnotation])
      .asScala
      .head
      .get(classOf[SentimentCoreAnnotations.SentimentAnnotatedTree])
    RNNCoreAnnotations.getPredictedClass(tree) 
  }  

  def sentiment(text: String): String =  
    calcSentiment(text) match {
        case 0 => "Very Negative"
        case 1 => "Negative" 
        case 2 => "Neutral" 
        case 3 => "Positive" 
        case 4 => "Very Positive"
    }
}

sqlc.udf.register("sentiment", Functions.sentiment _)
